import React,{Component} from 'react';
import { render } from '@testing-library/react';
import '../Styles/Location.css';



class Location extends Component{

    render(){
        function copyKS() {
            /* Get the text field */
            var copyText = document.getElementById("KSAddy");

            /* Select the text field */
            copyText.select();
            copyText.setSelectionRange(0, 99999); /* For mobile devices */

             /* Copy the text inside the text field */
            navigator.clipboard.writeText(copyText.value);

            /* Alert the copied text */
            alert("Copied the text: " + copyText.value);
          }
        return(

            <div className = "MainLocation" id = 'location'>
            <p className="locationTitle">visit us</p>

            <div className="locationBody">
                <div className="LocationDescription">
                <div className ="locationdesc">
                    <p className="LocationName" id = "OK">Oklahoma</p>
                    <p className = "Address">9373 S 4250 Rd, Inola, OK 74036</p>
                    <p className = "Number">(918) 543-3488</p>
                </div>

                <div className ="locationdesc">
                <p className="LocationName" id = "KS" onClick={copyKS()}>Kansas</p>
                <p className = "Address" id ="KSAddy">11491 SE Prairie Rd, Galena, KS 66739</p>
                <p className = "Number">(620) 856-3757</p>
                </div>
                </div>

                <div className="Map">

                </div>
            </div>

            </div>


        )
    }


}


export {Location};
